import network, urequests, ujson, machinefrom machine import PinledR = Pin(15, Pin.OUT)ledG = Pin(12, Pin.OUT)ledB = Pin(13, Pin.OUT) def turn(r, g, b):    ledR.value(r)    ledG.value(g)    ledB.value(b)    sta_if = network.WLAN(network.STA_IF)sta_if.active(True)sta_if.connect('Hengheng', '11111111') # 要連接的 WIFI 帳號與密碼while not sta_if.isconnected():    passprint(sta_if.ifconfig()[0])        # 顯示 IP 位址res = urequests.get(               # API 網址    "https://api.openweathermap.org/data/2.5/weather?" +    "q=" + "Nantou" + ",TW" +      # 指定城市與國別   "Nantou" + ",TW" or "Caotun" + ",TW"    "&units=metric&lang=zh_tw&" +  # 使用攝氏單位    "appid=" +  # 以下填入註冊後取得的 API key    "be95d10b9f29a0b5dfde30d109afb831")j = ujson.loads(res.text);         # 從 JSON 轉成字典gLED = machine.Pin(12, machine.Pin.OUT) # 控制綠燈rLED = machine.Pin(15, machine.Pin.OUT) # 控制紅燈weatherID = j["weather"][0]["id"]       # 天氣狀況代碼weatherDesc = j["weather"][0]["description"] # 天氣狀況if weatherID < 800:                # 雨天    turn(1,0,0)                  # 亮紅燈else:                              # 沒下雨    turn(0,1,0)                  # 亮綠燈    print("目前天氣：", str(weatherID))print("代碼意義：", weatherDesc )